package com.example.motionlayoutpractice

import android.content.Intent
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.View
import android.widget.Toast
import androidx.databinding.DataBindingUtil
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.motionlayoutpractice.data.ProfileData
import com.example.motionlayoutpractice.databinding.ActivityDataBindingBinding

class DataBindingActivity : AppCompatActivity() {

    // 파스칼 표기법에 기준해서 Activity~~Binding으로 찾아주면 됨
    private lateinit var binding : ActivityDataBindingBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        binding = DataBindingUtil.setContentView(this,R.layout.activity_data_binding)
        binding.activity = this@DataBindingActivity

        setRcv()

    }
    
    fun btnClick(view : View){
        startActivity(Intent(this,Binding2Activity::class.java))
    }

    fun setRcv(){
        val profileAdapter = ProfileAdapter(this)
        binding.mainRcv.layoutManager = LinearLayoutManager(this)
        binding.mainRcv.adapter = profileAdapter
        profileAdapter.data = listOf(
            ProfileData(profile = "data:image/jpeg;base64,", name = "Kang", age = 26),
            ProfileData(profile = "data:image/jpeg;base64,",name = "Kim", age = 25)
        )
        profileAdapter.notifyDataSetChanged()
    }
    
}